import * as React from 'react';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import './TabsMU.scss'
import {makeStyles} from "@mui/material";



function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Typography component={'ul'}>{children}</Typography>
                </Box>
            )}
        </div>
    );
}

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
        'color': 'white',
    };
}
const useStyles = makeStyles({
    root: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        border: 12,
        borderRadius: 3,
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        color: 'white',
        height: 48,
        padding: '0 30px',
    },
});

export default function BasicTabs() {
    const [value, setValue] = React.useState(0);
    const classes = useStyles()
    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
            <Box sx={{ width: '100%' }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider'}}>
                <Tabs
                    value={value}
                    onChange={handleChange}
                    allowScrollButtonsMobile={true}
                >
                    <Tab className={classes.root} label="Technical Skills" {...a11yProps(0)}/>
                    <Tab label=" Soft Skills" {...a11yProps(1)}/>
                    <Tab label="Education" {...a11yProps(2)}/>
                </Tabs>
            </Box>
            <TabPanel value={value} index={0}>
                <ul className='skills'>
                    <li>HTML 5</li>
                    <li>CSS(SCSS)</li>
                    <li>JS</li>
                    <li>React</li>
                    <li>Web-pack(basic)</li>
                    <li>MUI</li>
                    <li>jQuery</li>
                </ul>
            </TabPanel>
            <TabPanel value={value} index={1}>
                <ul className='skills'>
                    <li>Communication</li>
                    <li>Adaptability</li>
                    <li>Collaboration</li>
                    <li>Enthusiasm</li>
                    <li>Patience</li>
                    <li>Creativity</li>
                </ul>
            </TabPanel>
            <TabPanel value={value} index={2}>
                <div className='education'>
               <p>Kharkiv State Academy of Food Technology and Management</p>
                    <p>Commodity science and Commercial Activity</p>
                </div>
            </TabPanel>
        </Box>
    );
}
