import React, {useState} from 'react';
import '../../App.css'
import './Header.scss'

const Header = () => {
    let [active, setActive] = useState(false)

    let activeBurger = ['span_burger']
    let activeMenu = ['menu']

    if(active) {
        activeBurger.push('active_burger')
        activeMenu.push('active')
    }

    return (
        <header className='header'>
            <div className='container container__header' onClick={() => {setActive(!active)
            }}>
                <div className='header__logo'>
                    Portfolio
                </div>
                <div>
                    <div className="burger">
                        <span className={activeBurger.join(' ')}>
                        </span>
                    </div>
                    <ul className={activeMenu.join(' ')}>
                        <li onClick={() => {
                            setActive(!active)
                        }}><a href='#home'>Home</a></li>
                        <li onClick={() => {
                            setActive(!active)
                        }}><a href='#about'>About</a></li>
                        <li onClick={() => {
                            setActive(!active)
                        }}><a href='#projects'>Projects</a></li>
                        <li onClick={() => {
                            setActive(!active)
                        }}><a href='#contacts'>Contacts</a></li>
                    </ul>
                </div>
            </div>
        </header>

    );
};

export default Header;
